mapper.select:
SELECT 

FROM biz_sku_info a

WHERE a.id = #{id}

SELECT
FROM biz_sku_info a
a.status = #{DEL_FLAG_NORMAL}
AND a.id =#{id}
AND a.sku_type =#{skuType}
AND a.name LIKE '%'||#{name}||'%' '%'+#{name}+'%' concat('%',#{name},'%')
AND a.part_no LIKE '%'||#{partNo}||'%' '%'+#{partNo}+'%' concat('%',#{partNo},'%')
AND a.prod_id =#{productInfo.id}
AND bpi.name LIKE '%'||#{productInfo.name}||'%' '%'+#{productInfo.name}+'%' concat('%',#{productInfo.name},'%')
AND bpi.prod_code LIKE
'%'||#{productInfo.prodCode}||'%' '%'+#{productInfo.prodCode}+'%' concat('%',#{productInfo.prodCode},'%')
AND bpi.brand_name LIKE '%'||#{productInfo.brandName}||'%' '%'+#{productInfo.brandName}+'%' concat('%',#{productInfo.brandName},'%')
and a.id in
#{skuId}
ORDER BY ${page.orderBy}
ORDER BY a.update_time DESC

SELECT 

FROM biz_sku_info a


	a.status = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}

mapper.update:
UPDATE biz_sku_info SET prod_id = #{productInfo.id}, sku_type = #{skuType}, name = #{name}, part_no = #{partNo},
base_price = #{basePrice},
buy_price = #{buyPrice}, item_no = #{itemNo}, create_time = #{createDate}, u_version = #{uVersion}, update_id = #{updateBy.id}, update_time = #{updateDate} WHERE id = #{id}

UPDATE biz_sku_info SET 
	status = #{DEL_FLAG_DELETE}
WHERE id = #{id}
