mapper.select:
SELECT 

FROM biz_sku_info a

WHERE a.id = #{id}

SELECT , (select concat(ci.img_server, ci.img_path) FROM common_img ci WHERE ci.status = #{DEL_FLAG_NORMAL} AND ci.object_name = 'biz_sku_info' AND ci.object_id = a.id limit 1) AS defaultImg FROM biz_sku_info a
1=1
AND a.status = #{DEL_FLAG_NORMAL}
AND a.id =#{id}
AND a.sku_type =#{skuType}
AND a.name LIKE '%'||#{name}||'%' '%'+#{name}+'%' concat('%',#{name},'%')
AND a.part_no LIKE '%'||#{partNo}||'%' '%'+#{partNo}+'%' concat('%',#{partNo},'%')
AND a.item_no LIKE '%'||#{itemNo}||'%' '%'+#{itemNo}+'%' concat('%',#{itemNo},'%')
AND a.prod_id =#{productInfo.id}
AND bpi.prod_type =#{productInfo.prodType}
AND bpi.name LIKE '%'||#{productInfo.name}||'%' '%'+#{productInfo.name}+'%' concat('%',#{productInfo.name},'%')
AND bpi.vendor_name LIKE '%'||#{productInfo.vendorName}||'%' '%'+#{productInfo.vendorName}+'%' concat('%',#{productInfo.vendorName},'%')
AND bpi.prod_code LIKE
'%'||#{productInfo.prodCode}||'%' '%'+#{productInfo.prodCode}+'%' concat('%',#{productInfo.prodCode},'%')
AND bpi.brand_name LIKE '%'||#{productInfo.brandName}||'%' '%'+#{productInfo.brandName}+'%' concat('%',#{productInfo.brandName},'%')
and a.id in
#{skuId}
ORDER BY ${page.orderBy}
ORDER BY a.update_time DESC

