AND bpi.prod_code LIKE
'%'||#{productInfo.prodCode}||'%' '%'+#{productInfo.prodCode}+'%' concat('%',#{productInfo.prodCode},'%')
AND bpi.brand_name LIKE '%'||#{productInfo.brandName}||'%' '%'+#{productInfo.brandName}+'%' concat('%',#{productInfo.brandName},'%')
and a.id in
#{skuId}
ORDER BY ${page.orderBy}
