mapper.select:
SELECT a.id AS "id", a.name as "name", a.item_no as "itemNo", bod.buy_price as "orderDetail.buyPrice", bod.unit_price as "orderDetail.unitPrice", bod.ord_qty AS "orderDetail.ordQty", (select sur.name from biz_order_header bo, sys_office so, biz_custom_center_consultant bc, sys_user sur where bo.cust_id != 0 and bo.cust_id = so.id and bc.cust_id = so.id and bc.consultant_id = sur.id and sur.status = 1 and bo.id = boh.id )as "custName", boh.id as "orderId", boh.order_num as "orderNum", boh.order_type as "orderHeader.orderType", s.name AS "centersName", bis.stock_qty AS "inventorySku.stockQty" FROM biz_sku_info a left join biz_order_detail bod on bod.sku_id = a.id left join biz_order_header boh on boh.id = bod.order_id AND boh.order_type != 4 AND NOT EXISTS ( SELECT 1 FROM sys_test_account ta WHERE boh.cust_id = ta.object_id AND ta.object_name = 'sys_office' ) LEFT JOIN biz_custom_center_consultant ccs ON ccs.cust_id = boh.cust_id LEFT JOIN sys_user su ON su.id = ccs.consultant_id LEFT JOIN sys_office s ON s.id = ccs.center_id LEFT JOIN biz_inventory_info i ON i.cust_id=s.id LEFT JOIN biz_inventory_sku bis on bis.inv_id=i.id and bis.sku_id=bod.sku_id and bis.status=1 WHERE a.status = #{DEL_FLAG_NORMAL} AND (boh.order_num != NULL OR boh.order_num != "")
AND a.name LIKE '%'||#{name}||'%' '%'+#{name}+'%' concat('%',#{name},'%')
AND a.item_no LIKE '%'||#{itemNo}||'%' '%'+#{itemNo}+'%' concat('%',#{itemNo},'%')
AND boh.order_num LIKE '%'||#{orderNum}||'%' '%'+#{orderNum}+'%' concat('%',#{orderNum},'%')
AND s.name LIKE '%'||#{centersName}||'%' '%'+#{centersName}+'%' concat('%',#{centersName},'%')
AND boh.create_time >= #{orderCreatStartTime}
AND boh.create_time <= #{orderCreatEndTime}
${sqlMap.chat}
group by a.id,boh.order_num
ORDER BY ${page.orderBy}
ORDER BY a.id DESC
