mapper.select:
SELECT 

FROM biz_sku_prop_value a

WHERE a.id = #{id}

SELECT 

FROM biz_sku_prop_value a


	a.status = #{DEL_FLAG_NORMAL}

		AND a.sku_id = #{skuId}


		AND a.prop_name = #{propName}




		ORDER BY ${page.orderBy}

SELECT 

FROM biz_sku_prop_value a


	a.status = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}

mapper.update:
UPDATE biz_sku_prop_value SET 
	status = #{DEL_FLAG_DELETE}
WHERE id = #{id}
