mapper.select:
SELECT 

FROM biz_vend_info a

WHERE a.office_id = #{id}

SELECT 

FROM biz_vend_info a


	a.status = #{DEL_FLAG_NORMAL}

		AND a.vend_name = #{vendName}


		AND a.cate_name = #{cateName}




		ORDER BY ${page.orderBy}

SELECT 

FROM biz_vend_info a


	a.status = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}

mapper.update:
UPDATE biz_vend_info SET 
	status = #{DEL_FLAG_DELETE}
WHERE id = #{id}
