mapper.select:
SELECT 

FROM common_dynamic_config a

WHERE a.id = #{id}

SELECT 

FROM common_dynamic_config a


	a.status = #{DEL_FLAG_NORMAL}

		AND a.id = #{id}


		AND a.confname like
		'%'||#{confname}||'%'
		'%'+#{confname}+'%'
		concat('%',#{confname},'%')


		AND a.content = #{content}



		AND a.status = #{status}


		AND a.version = #{version}


		AND a.updated = #{updated}




		ORDER BY ${page.orderBy}


		a.confname , a.version desc

SELECT 

FROM common_dynamic_config a


	a.status = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}

mapper.update:
UPDATE common_dynamic_config SET 	
	confname = #{confname},
	content = #{content},
	version = #{version},
	updated = #{updated}
WHERE id = #{id}

UPDATE common_dynamic_config SET 
	status = #{DEL_FLAG_DELETE}
WHERE id = #{id}
