mapper.select:
SELECT 

FROM sys_default_prop a

WHERE a.id = #{id}

SELECT 

FROM sys_default_prop a


	a.status = #{DEL_FLAG_NORMAL}

		AND a.prop_key = #{propKey}




		ORDER BY ${page.orderBy}

SELECT 

FROM sys_default_prop a


	a.status = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}

mapper.update:
UPDATE sys_default_prop SET 
	status = #{DEL_FLAG_DELETE}
WHERE id = #{id}
