select *
from sys_office a
where FIND_IN_SET(id,
(select GROUP_CONCAT(distinct parent_ids,'0')
from biz_custom_center_consultant ccc, sys_office so
where ccc.cust_id = so.id and ccc.status=1

	AND ccc.cust_id = #{customs.id}


	AND ccc.center_id = #{centers.id}


	AND ccc.consultant_id = #{consultants.id}

))
union all
select so.*
from biz_custom_center_consultant ccc, sys_office so
where ccc.cust_id = so.id and so.status=1 and  ccc.status=1

	AND ccc.cust_id = #{customs.id}


	AND ccc.center_id = #{centers.id}


	AND ccc.consultant_id = #{consultants.id}


	AND so.parent_ids LIKE #{parentIds}

order by parent_ids

SELECT * FROM sys_office a WHERE FIND_IN_SET( id, ( SELECT GROUP_CONCAT(distinct parent_ids, '0' ) FROM sys_office so WHERE so.status = #{DEL_FLAG_NORMAL}
AND so.type = #{type}
AND NOT EXISTS ( SELECT ccc.cust_id FROM biz_custom_center_consultant ccc WHERE ccc.cust_id = so.id and ccc.status=1 and ccc.consultant_id != (SELECT prop_value FROM sys_default_prop WHERE prop_desc = '超级客户专员')) ) ) UNION ALL SELECT so.* FROM sys_office so WHERE so.status = #{DEL_FLAG_NORMAL}
AND so.type = #{type}
AND NOT EXISTS ( SELECT ccc.cust_id FROM biz_custom_center_consultant ccc WHERE ccc.cust_id = so.id and ccc.status=1 and ccc.consultant_id != (SELECT prop_value FROM sys_default_prop WHERE prop_desc = '超级客户专员'))

