mapper.select:
SELECT 

FROM sys_prop_value a

WHERE a.id = #{id}

SELECT 

FROM sys_prop_value a


	a.status = #{DEL_FLAG_NORMAL}

		and a.prop_id=#{propertyInfo.id}




		ORDER BY ${page.orderBy}

SELECT 

FROM sys_prop_value a


	a.status = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}

mapper.update:
UPDATE sys_prop_value SET 
	status = #{DEL_FLAG_DELETE}
WHERE prop_id = #{propertyInfo.id}
