mapper.select:
SELECT 

FROM sys_property_info a

WHERE a.id = #{id}

SELECT
FROM sys_property_info a
a.status = #{DEL_FLAG_NORMAL}
AND a.name LIKE '%'||#{name}||'%' '%'+#{name}+'%' concat('%',#{name},'%')
ORDER BY ${page.orderBy}
ORDER by updateDate desc

SELECT 

FROM sys_property_info a


	a.status = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}

mapper.update:
UPDATE sys_property_info SET 
	status = #{DEL_FLAG_DELETE}
WHERE id = #{id}
