mapper.select:
SELECT 

FROM sys_attribute_value a

WHERE a.id = #{id}

SELECT 

FROM sys_attribute_value a


	a.status = #{DEL_FLAG_NORMAL}

		AND a.attr_id = #{attrId}


		AND a.object_name = #{objectName}


		AND a.object_id = #{objectId}




		ORDER BY ${page.orderBy}

SELECT 

FROM sys_attribute_value a


	a.status = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}

mapper.update:
UPDATE sys_attribute_value SET 
	status = #{DEL_FLAG_DELETE}
WHERE id = #{id}
