mapper.select:
SELECT 

FROM sys_tag_info a

WHERE a.id = #{id}

SELECT 

FROM sys_tag_info a


	a.status = #{DEL_FLAG_NORMAL}

		AND a.name LIKE 
			'%'||#{name}||'%'
			'%'+#{name}+'%'
			concat('%',#{name},'%')


		and a.LEVEL = #{level}




		ORDER BY ${page.orderBy}

SELECT 

FROM sys_tag_info a


	a.status = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}

mapper.update:
UPDATE sys_tag_info SET 
	status = #{DEL_FLAG_DELETE}
WHERE id = #{id}
